import {Link, useParams} from "react-router-dom";
import {useEffect, useState} from "react";
import axios from "axios";
import BookingWidget from "../BookingWidget";

export default function PortfolioPage() {
  const {id} = useParams();
  const [portfolio,setPortfolio] = useState(null);
  useEffect(() => {
    if (!id) {
      return;
    }
    axios.get(`/portfolios/${id}`).then(response => {
      setPortfolio(response.data);
    });
  }, [id]);

  if (!portfolio) return '';

  return (
    <div className="mt-4 bg-gray-100 -mx-8 px-8 pt-8">
      <h1 className="text-3xl">{portfolio.title}</h1>
      <h2 className="text-3xl">{portfolio.address}</h2>
      <div className="mt-8 mb-8 grid gap-8 grid-cols-1 md:grid-cols-[2fr_1fr]">
        <div>
          <div className="my-4">
            <h2 className="font-semibold text-2xl">Description</h2>
            {portfolio.description}
          </div>
        </div>
        <div>
          <BookingWidget portfolio={portfolio} />
        </div>
      </div>
      <div className="bg-white -mx-8 px-8 py-8 border-t">
        <div>
          <h2 className="font-semibold text-2xl">Price</h2>
        </div>
        <div className="mb-4 mt-2 text-sm text-gray-700 leading-5">{portfolio.price}</div>
      </div>
    </div>
  );
}
