import { useContext, useState, useEffect } from "react";
import { StylistContext } from "../StylistContext";
import { Link, Navigate, useParams } from "react-router-dom";
import StylistAccountNav from "../StylistAccountNav";
import axios from "axios";

export default function StylistProfilePage() {
  const [redirect, setRedirect] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const { ready, stylist, setStylist } = useContext(StylistContext);
  let { subpage } = useParams();
  if (subpage === undefined) {
    subpage = '/stylistaccount';
  }

  async function stylistlogout() {
    await axios.post('/stylistlogout');
    setRedirect('/');
    setStylist(null);
  }

  useEffect(() => {
    if (isLoggedIn) {
      axios.get('/stylistaccount').then(({data}) => {
        setStylist(data);
      }).finally(() => {
        setIsLoggedIn(false);
      });
    }
  }, [isLoggedIn]);

  if (!ready) {
    return 'Loading...';
  }

  if (ready && !stylist && !redirect) {
    return <Navigate to={'/stylistlogin'} />
  }

  if (redirect) {
    return <Navigate to={redirect} />
  }

  return (
    <div>
        <StylistAccountNav />
      {subpage === '/stylistaccount' && (
        <div className="text-center max-w-lg mx-auto">
          Logged in as {stylist.name}<br />
          <button onClick={stylistlogout} className="primary max-w-sm mt-2">Logout</button>
        </div>
      )}
    </div>
  );
}
