import {useContext, useState} from "react";
import { StylistContext } from "../StylistContext";
import {Link, Navigate, useParams} from "react-router-dom";
import axios from "axios";

export default function StylistProfilePage() {
  const [redirect, setRedirect] = useState(null);
  const {stylist, setStylist} = useContext(StylistContext);
  let {subpage} = useParams();
  if (subpage === undefined) {
    subpage = 'stylistprofile';
  }

  async function logout() {
    await axios.post('/logout');
    setRedirect('/');
    setStylist(null);
  }

  if (!stylist) {
    return 'Loading...';
  }

  if (stylist && !redirect) {
    return <Navigate to={'/stylistlogin'} />
  }

  if (redirect) {
    return <Navigate to={redirect} />
  }
  return (
    <div>
      {subpage === 'stylistprofile' && (
        <div className="text-center max-w-lg mx-auto">
          Logged in as {stylist.name} ({stylist.email})<br />
          <button onClick={logout} className="primary max-w-sm mt-2">Logout</button>
        </div>
      )}
      {subpage === 'places' && (
        <div>
          <h2 className="text-center mt-4 mb-2 text-2xl font-bold">Your Places</h2>
          {/* Stylist's Places */}
        </div>
      )}
    </div>
  );
}
